sys_script_include.script:
var NeeditUtils = Class.create();
NeeditUtils.prototype = {
	initialize: function() {
	},
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},
	isTodayOrPast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var testdate = new GlideDateTime(strDate);
		var tomorrow = new GlideDateTime();
		tomorrow.addDaysLocalTime(1);

		// If the testdate is today or before rightnow, return true, else return false
		if (testdate.before(tomorrow.getLocalDate())) {
			return true;
		}
		else {
			return false;
		}
	},
	type: 'NeeditUtils'
};
